		{
			float scalar = 24.0f;
			Sharp3D.Math.Core.Vector2F vector = new Vector2F(2, 3);

			Sharp3D.Math.Core.Vector2F expected = new Vector2F(12, 8);
			Sharp3D.Math.Core.Vector2F actual;

			actual = scalar / vector;
			Assert.AreEqual(expected, actual);
		}
